// Button data class to define each button's properties
data class ButtonItem(
    val label: Int,
    val color: Color,
    val shape: Shape,
    val hint: List<String>
)

// Helper function to generate hints for each button
fun getHintsForButton(buttonNumber: Int): List<String> {
    val rowHint = when {
        buttonNumber <= 5 -> "Top row"
        buttonNumber <= 10 -> "Second row"
        buttonNumber <= 15 -> "Third row"
        buttonNumber <= 20 -> "Fourth row"
        else -> "Bottom row"
    }

    val columnHint = when {
        buttonNumber % 5 == 1 -> "First column"
        buttonNumber % 5 == 2 -> "Second column"
        buttonNumber % 5 == 3 -> "Third column"
        buttonNumber % 5 == 4 -> "Fourth column"
        else -> "Fifth column"
    }

    val colorHint = when (buttonNumber % 6) {
        0 -> "Purple"
        1 -> "Pink"
        2 -> "Blue"
        3 -> "Green"
        4 -> "Orange"
        else -> "Red"
    }

    val shapeHint = when (buttonNumber % 4) {
        0 -> "Circular"
        1 -> "Sharp corners"
        2 -> "Moderately rounded"
        else -> "Very rounded"
    }

    return listOf(rowHint, columnHint, colorHint, shapeHint)
}

class SassySwitches : ComponentActivity() {
    private lateinit var soundManager: SoundManager

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        soundManager = SoundManager(this)
        enableEdgeToEdge()
        setContent {
            val systemDarkTheme = isSystemInDarkTheme()
            var isDarkTheme by rememberSaveable {
                mutableStateOf(systemDarkTheme)
            }

            DontPushMyButtonsTheme(darkTheme = isDarkTheme) {
                Surface(
                    modifier = Modifier.fillMaxSize(),
                    color = MaterialTheme.colorScheme.background
                ) {
                    SassySwitchesGame(
                        isDarkTheme = isDarkTheme,
                        onThemeChange = { isDarkTheme = it },
                        soundManager = soundManager
                    )
                }
            }
        }
    }

    override fun onDestroy() {
        super.onDestroy()
        soundManager.release()
    }
}

@Composable
fun SassySwitchesGame(
    isDarkTheme: Boolean,
    onThemeChange: (Boolean) -> Unit,
    soundManager: SoundManager
) {
    // Landing page state
    var isGameStarted by remember { mutableStateOf(false) }
    var showHowToDialog by remember { mutableStateOf(false) }

    if (!isGameStarted) {
        // Landing Page
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(16.dp),
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            // Header with theme switch
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.End,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = if (isDarkTheme) "Dark" else "Light",
                    style = MaterialTheme.typography.bodyMedium
                )
                Spacer(modifier = Modifier.width(8.dp))
                Switch(
                    checked = isDarkTheme,
                    onCheckedChange = onThemeChange
                )
            }

            Spacer(modifier = Modifier.weight(0.3f))

            AppLogo(modifier = Modifier.size(360.dp))

            Spacer(modifier = Modifier.height(32.dp))

            Button(
                onClick = { isGameStarted = true },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(56.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.primary
                )
            ) {
                Text(
                    text = "Start Game",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )
            }

            Spacer(modifier = Modifier.height(16.dp))

            Button(
                onClick = { showHowToDialog = true },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(56.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.secondary
                )
            ) {
                Text(
                    text = "How To Play",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )
            }

            Spacer(modifier = Modifier.weight(0.2f))
        }

        // How To Dialog
        if (showHowToDialog) {
            AlertDialog(
                onDismissRequest = { showHowToDialog = false },
                title = {
                    Text(
                        text = "How to Play Sassy Switches",
                        style = MaterialTheme.typography.headlineSmall,
                        fontWeight = FontWeight.Bold
                    )
                },
                text = {
                    Text(
                        text = "1. Find the hidden target button among the 25 buttons\n\n" +
                                "2. Each wrong guess increases your click count\n\n" +
                                "3. After every 5 wrong guesses, you'll get a hint\n\n" +
                                "4. Find the target button as quickly as possible!\n\n" +
                                "5. Your score is the number of clicks it takes to find the target",
                        style = MaterialTheme.typography.bodyMedium
                    )
                },
                confirmButton = {
                    Button(
                        onClick = { showHowToDialog = false }
                    ) {
                        Text("Got it!")
                    }
                }
            )
        }
    } else {
        // Game Screen
        GameScreen(
            isDarkTheme = isDarkTheme,
            onThemeChange = onThemeChange,
            soundManager = soundManager,
            onBackToLanding = { isGameStarted = false }
        )
    }
}

@Composable
fun GameScreen(
    isDarkTheme: Boolean,
    onThemeChange: (Boolean) -> Unit,
    soundManager: SoundManager,
    onBackToLanding: () -> Unit
) {
    var counter by remember { mutableStateOf(0) }
    var gameOver by remember { mutableStateOf(false) }
    var currentHint by remember { mutableStateOf<String?>(null) }
    var targetButtonId by remember { mutableStateOf(0) }
    var finalScore by remember { mutableStateOf(0) }
    var incorrectClicks by remember { mutableStateOf(0) }

    // Create 25 buttons with different colors and shapes
    val items: List<ButtonItem> = remember {
        (1..25).map { buttonNumber ->
            ButtonItem(
                label = buttonNumber,
                color = when (buttonNumber % 6) {
                    0 -> Color(0xFF9C27B0) // Purple
                    1 -> Color(0xFFE91E63) // Pink
                    2 -> Color(0xFF2196F3) // Blue
                    3 -> Color(0xFF4CAF50) // Green
                    4 -> Color(0xFFFF9800) // Orange
                    else -> Color(0xFFF44336) // Red
                },
                shape = when (buttonNumber % 4) {
                    0 -> androidx.compose.foundation.shape.CircleShape
                    1 -> androidx.compose.foundation.shape.RoundedCornerShape(4.dp)
                    2 -> androidx.compose.foundation.shape.RoundedCornerShape(12.dp)
                    else -> androidx.compose.foundation.shape.RoundedCornerShape(20.dp)
                },
                hint = getHintsForButton(buttonNumber)
            )
        }
    }

    fun updateHint() {
        if (incorrectClicks % 5 == 0 && incorrectClicks > 0) {
            val hintIndex = (incorrectClicks / 5) - 1
            val targetButton = items.find { it.label == targetButtonId }
            targetButton?.let {
                if (hintIndex < it.hint.size) {
                    currentHint = it.hint[hintIndex]
                }
            }
        }
    }

    fun startNewGame() {
        gameOver = false
        counter = 0
        incorrectClicks = 0
        currentHint = null
        targetButtonId = (1..25).random() // Select random target button from 1-25
    }

    // Initialize game when component is first created
    LaunchedEffect(Unit) {
        startNewGame()
    }

    Column(modifier = Modifier.padding(16.dp)) {
        // Header with SassySwitches logo and theme toggle
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 16.dp, vertical = 2.dp),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            // SassySwitches logo on game page
            AppLogo(modifier = Modifier.size(120.dp))

            if (!gameOver) {
                Text(
                    text = "Clicks: $counter",
                    style = MaterialTheme.typography.headlineSmall
                )
            }

            Row(
                verticalAlignment = Alignment.CenterVertically
            ) {
                Switch(
                    checked = isDarkTheme,
                    onCheckedChange = onThemeChange
                )
            }
        }

        if (gameOver) {
            // Game Over Screen
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(16.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                Text(
                    text = "Ok! Ok! You found me!",
                    style = MaterialTheme.typography.headlineMedium,
                    modifier = Modifier.padding(bottom = 8.dp)
                )
                Text(
                    text = "Now stop pushing my buttons!",
                    style = MaterialTheme.typography.headlineMedium,
                    modifier = Modifier.padding(bottom = 16.dp)
                )
                Text(
                    text = "Final Score: $finalScore clicks",
                    style = MaterialTheme.typography.headlineSmall,
                    color = MaterialTheme.colorScheme.primary,
                    fontWeight = FontWeight.Bold,
                    modifier = Modifier.padding(bottom = 16.dp)
                )
                Button(
                    onClick = { startNewGame() },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                ) {
                    Text("Play Again")
                }

                Spacer(modifier = Modifier.height(16.dp))

                Button(
                    onClick = onBackToLanding,
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp),
                    colors = ButtonDefaults.buttonColors(
                        containerColor = MaterialTheme.colorScheme.secondary
                    )
                ) {
                    Text("Back to Menu")
                }
            }
        } else {
            // Active Game screen
            Box(modifier = Modifier.fillMaxWidth()) {
                currentHint?.let { hint ->
                    Text(
                        text = "Hint: $hint",
                        style = MaterialTheme.typography.bodyLarge,
                        fontSize = 20.sp,
                        color = MaterialTheme.colorScheme.primary,
                        fontWeight = FontWeight.Bold,
                        modifier = Modifier.padding(16.dp)
                    )
                }
            }

            LazyVerticalGrid(
                columns = GridCells.Fixed(5),
                contentPadding = PaddingValues(8.dp),
                verticalArrangement = Arrangement.spacedBy(8.dp),
                horizontalArrangement = Arrangement.spacedBy(8.dp),
                modifier = Modifier.fillMaxSize()
            ) {
                items(items) { item ->
                    ButtonGridItem(
                        label = item.label,
                        color = item.color,
                        shape = item.shape,
                        onClickIncrement = {
                            counter++
                            val isCorrectButton = item.label == targetButtonId

                            if (isCorrectButton) {
                                finalScore = counter
                                gameOver = true
                                // soundManager.playCorrectButtonSound()
                            } else {
                                incorrectClicks++
                                updateHint()
                                // soundManager.playButtonClickSound()
                            }
                        }
                    )
                }
            }
        }
    }
}

@Composable
fun ButtonGridItem(label: Int, color: Color, shape: Shape, onClickIncrement: () -> Unit) {
    Button(
        onClick = onClickIncrement,
        colors = ButtonDefaults.buttonColors(containerColor = color),
        shape = shape,
        modifier = Modifier
            .fillMaxWidth()
            .aspectRatio(1f)
    ) {
        Text(
            text = label.toString(),
            style = MaterialTheme.typography.headlineSmall,
            color = MaterialTheme.colorScheme.onPrimary
        )
    }
}

