    val context = LocalContext.current

    Image(
        painter = painterResource(id = R.drawable.sassyswitches),
        contentDescription = "SassySwitches Logo",
        modifier = modifier
            .clickable {
                val intent = Intent(context, HomeActivity::class.java)
                context.startActivity(intent)
            }
    )
}

