@Composable
fun SassySwitchesGame(
    isDarkTheme: Boolean,
    onThemeChange: (Boolean) -> Unit,
    soundManager: SoundManager
) {
    // Landing page state
    var isGameStarted by remember { mutableStateOf(false) }
    var showHowToDialog by remember { mutableStateOf(false) }

    // Game state
    var targetButton by remember { mutableStateOf<Int?>(null) }
    var clickCount by remember { mutableStateOf(0) }
    var gameOver by remember { mutableStateOf(false) }
    var hintLevel by remember { mutableStateOf(0) }
    var currentHint by remember { mutableStateOf("") }

    // Game initialization
    LaunchedEffect(isGameStarted) {
        if (isGameStarted && targetButton == null) {
            targetButton = (1..25).random()
            clickCount = 0
            gameOver = false
            hintLevel = 0
            currentHint = ""
        }
    }

    if (!isGameStarted) {
        // Landing Page
        Column(
            modifier = Modifier
                .fillMaxSize()
                .padding(24.dp),
            horizontalAlignment = Alignment.CenterHorizontally,
            verticalArrangement = Arrangement.Center
        ) {
            // Theme toggle at the top
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(bottom = 32.dp),
                horizontalArrangement = Arrangement.End,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text(
                    text = if (isDarkTheme) "Dark" else "Light",
                    style = MaterialTheme.typography.bodyMedium
                )
                Spacer(modifier = Modifier.width(8.dp))
                Switch(
                    checked = isDarkTheme,
                    onCheckedChange = onThemeChange
                )
            }

            Spacer(modifier = Modifier.weight(0.3f))

            // Logo - using consistent 360dp size to match other games
            AppLogo(modifier = Modifier.size(360.dp))

            Spacer(modifier = Modifier.height(32.dp))

            // Start Game button - CONSISTENT SIZING with other games
            Button(
                onClick = { isGameStarted = true },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(56.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.primary
                )
            ) {
                Text(
                    text = "Start Game",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )
            }

            Spacer(modifier = Modifier.height(16.dp))

            // How to Play button - CONSISTENT SIZING with other games
            Button(
                onClick = { showHowToDialog = true },
                modifier = Modifier
                    .fillMaxWidth()
                    .height(56.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = MaterialTheme.colorScheme.secondary
                )
            ) {
                Text(
                    text = "How to Play",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold
                )
            }

            Spacer(modifier = Modifier.weight(0.7f))
        }

        // How to Play Dialog
        if (showHowToDialog) {
            AlertDialog(
                onDismissRequest = { showHowToDialog = false },
                title = {
                    Text(
                        text = "How to Play SassySwitches",
                        fontWeight = FontWeight.Bold
                    )
                },
                text = {
                    Column {
                        Text(" Find the hidden target button among the 25 buttons")
                        Text(" Each wrong guess increases your click count")
                        Text(" After every 5 wrong guesses, you'll get a hint")
                        Text(" Find the target button as quickly as possible!")
                        Text(" Your score is the number of clicks it takes to find the target")
                    }
                },
                confirmButton = {
                    Button(
                        onClick = { showHowToDialog = false }
                    ) {
                        Text("Got it!")
                    }
                }
            )
        }
    } else {
        // Game Screen
        if (gameOver) {
            // Game Over Screen
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(24.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                // Theme toggle at the top
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(bottom = 16.dp),
                    horizontalArrangement = Arrangement.End,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    Text(
                        text = if (isDarkTheme) "Dark" else "Light",
                        style = MaterialTheme.typography.bodyMedium
                    )
                    Spacer(modifier = Modifier.width(8.dp))
                    Switch(
                        checked = isDarkTheme,
                        onCheckedChange = onThemeChange
                    )
                }

                AppLogo(modifier = Modifier.size(120.dp))

                Spacer(modifier = Modifier.height(32.dp))

                Text(
                    text = "Congratulations!",
                    style = MaterialTheme.typography.headlineMedium,
                    fontWeight = FontWeight.Bold
                )

                Spacer(modifier = Modifier.height(16.dp))

                Text(
                    text = "You found the target button!",
                    style = MaterialTheme.typography.titleMedium
                )

                Spacer(modifier = Modifier.height(16.dp))

                Text(
                    text = "Final Score: $clickCount clicks",
                    style = MaterialTheme.typography.titleLarge,
                    fontWeight = FontWeight.Bold,
                    color = MaterialTheme.colorScheme.primary
                )

                Spacer(modifier = Modifier.height(32.dp))

                Button(
                    onClick = {
                        // Reset game
                        isGameStarted = false
                        targetButton = null
                        clickCount = 0
                        gameOver = false
                        hintLevel = 0
                        currentHint = ""
                    },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                ) {
                    Text(
                        text = "Play Again",
                        style = MaterialTheme.typography.titleMedium,
                        fontWeight = FontWeight.Bold
                    )
                }
            }
        } else {
            // Active Game Screen
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(16.dp)
            ) {
                // Header with theme toggle and logo
                Row(
                    modifier = Modifier
                        .fillMaxWidth()
                        .padding(bottom = 16.dp),
                    horizontalArrangement = Arrangement.SpaceBetween,
                    verticalAlignment = Alignment.CenterVertically
                ) {
                    AppLogo(modifier = Modifier.size(120.dp))

                    Column(
                        horizontalAlignment = Alignment.End
                    ) {
                        Row(
                            verticalAlignment = Alignment.CenterVertically
                        ) {
                            Text(
                                text = if (isDarkTheme) "Dark" else "Light",
                                style = MaterialTheme.typography.bodyMedium
                            )
                            Spacer(modifier = Modifier.width(8.dp))
                            Switch(
                                checked = isDarkTheme,
                                onCheckedChange = onThemeChange
                            )
                        }

                        // Display hint if available
                        if (currentHint.isNotEmpty()) {
                            Spacer(modifier = Modifier.height(8.dp))
                            Text(
                                text = "Hint: $currentHint",
                                style = MaterialTheme.typography.bodyMedium,
                                color = MaterialTheme.colorScheme.primary,
                                fontWeight = FontWeight.Bold
                            )
                        }
                    }
                }

                // Click counter
                Text(
                    text = "Clicks: $clickCount",
                    style = MaterialTheme.typography.titleMedium,
                    fontWeight = FontWeight.Bold,
                    modifier = Modifier.padding(bottom = 16.dp)
                )

                // Grid of buttons
                LazyVerticalGrid(
                    columns = GridCells.Fixed(5),
                    contentPadding = PaddingValues(8.dp),
                    verticalArrangement = Arrangement.spacedBy(8.dp),
                    horizontalArrangement = Arrangement.spacedBy(8.dp)
                ) {
                    items((1..25).toList()) { buttonNumber ->
                        ButtonGridItem(
                            label = buttonNumber,
                            color = when (buttonNumber % 6) {
                                0 -> Color(0xFF9C27B0) // Purple
                                1 -> Color(0xFFE91E63) // Pink
                                2 -> Color(0xFF2196F3) // Blue
                                3 -> Color(0xFF4CAF50) // Green
                                4 -> Color(0xFFFF9800) // Orange
                                else -> Color(0xFFF44336) // Red
                            },
                            shape = when (buttonNumber % 4) {
                                0 -> androidx.compose.foundation.shape.CircleShape
                                1 -> androidx.compose.foundation.shape.RoundedCornerShape(4.dp)
                                2 -> androidx.compose.foundation.shape.RoundedCornerShape(12.dp)
                                else -> androidx.compose.foundation.shape.RoundedCornerShape(20.dp)
                            }
                        ) {
                            clickCount++

                            if (buttonNumber == targetButton) {
                                // Found the target button!
                                gameOver = true
                            } else {
                                // Wrong button, check if hint should be shown
                                if (clickCount % 5 == 0) {
                                    hintLevel++
                                    currentHint = getHintForButton(targetButton ?: 1, hintLevel)
                                }
                            }
                        }
                    }
                }
            }
        }
    }
}

// Helper function to get hints for specific buttons
fun getHintForButton(buttonNumber: Int, hintLevel: Int): String {
    return when (hintLevel) {
        1 -> when {
            buttonNumber <= 5 -> "The target is in the top row"
            buttonNumber <= 10 -> "The target is in the second row"
            buttonNumber <= 15 -> "The target is in the third row"
            buttonNumber <= 20 -> "The target is in the fourth row"
            else -> "The target is in the bottom row"
        }
        2 -> when {
            buttonNumber % 5 == 1 -> "The target is in the first column"
            buttonNumber % 5 == 2 -> "The target is in the second column"
            buttonNumber % 5 == 3 -> "The target is in the third column"
            buttonNumber % 5 == 4 -> "The target is in the fourth column"
            else -> "The target is in the fifth column"
        }
        3 -> when (buttonNumber % 6) {
            0 -> "The target button is purple"
            1 -> "The target button is pink"
            2 -> "The target button is blue"
            3 -> "The target button is green"
            4 -> "The target button is orange"
            else -> "The target button is red"
        }
        4 -> when (buttonNumber % 4) {
            0 -> "The target button is circular"
            1 -> "The target button has sharp corners"
            2 -> "The target button is moderately rounded"
            else -> "The target button is very rounded"
        }
        else -> "The target button is number $buttonNumber"
    }
}

